    private void kaliActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_kaliActionPerformed
        // TODO add your handling code here:
        try {
                double nilai1 = Double.parseDouble(input1.getText());
                double nilai2 = Double.parseDouble(input2.getText());
                double hsl = nilai1 * nilai2;
                if (hsl == Math.floor(hsl) && !Double.isInfinite(hsl)) {
                    // kalo bil bulat ilangin koma
                    hasil.setText(Long.toString((long) hsl));
                } else {
                    // Jika hasilnya bukan bilangan bulat, tampilkan dengan angka desimal
                    hasil.setText(Double.toString(hsl));
                }
            } catch (NumberFormatException ex) {
                // Tangani input yang tidak valid
                hasil.setText("input salah");
                }
    }//GEN-LAST:event_kaliActionPerformed

    private void bagiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bagiActionPerformed
        // TODO add your handling code here:
      try {
    double nilai1 = Double.parseDouble(input1.getText());
    double nilai2 = Double.parseDouble(input2.getText());

    if (nilai2 != 0) {
        double hsl = nilai1 / nilai2;

        if (hsl == Math.floor(hsl) && !Double.isInfinite(hsl)) {
            hasil.setText(Long.toString((long) hsl));
        } else {
            hasil.setText(Double.toString(hsl));
        }
    } else {
        hasil.setText("Error: Division by zero");
    }
} catch (NumberFormatException ex) {
    hasil.setText("Invalid input");
}
    }//GEN-LAST:event_bagiActionPerformed

