    private void kurangActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_kurangActionPerformed
        // TODO add your handling code here:
        double hsl;
            hsl = Double.parseDouble(input1.getText())- Double.parseDouble(input2.getText());
            hasil.setText(Double.toString(hsl));
    }//GEN-LAST:event_kurangActionPerformed

    private void modulusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_modulusActionPerformed
        // TODO add your handling code here:
        try {
                    // Ambil input dari dua JTextField, input1 dan input2
                    double nilai1 = Double.parseDouble(input1.getText());
                    double nilai2 = Double.parseDouble(input2.getText());

                    // ngecek nilai nol apa ngga
                    if (nilai2 != 0) {
                        // Lakukan operasi modulus
                        double hsl = nilai1 % nilai2;

                        // kalo bilangan bulat nol nya ilang
                        if (hsl == Math.floor(hsl) && !Double.isInfinite(hsl)) {
                            // Jika bilangan bulat, tampilkan tanpa angka desimal
                            hasil.setText(Long.toString((long) hsl));
                        } else {
                            // Jika bukan bilangan bulat, tampilkan dengan angka desimal
                            hasil.setText(Double.toString(hsl));
                        }
                    } else {
                        // Jika nilai2 adalah nol, tampilkan pesan error
                        hasil.setText("Error: Division by zero");
                    }
                } catch (NumberFormatException ex) {
                    // Tangani input yang tidak valid
                    hasil.setText("Invalid input");
                }
            
    }//GEN-LAST:event_modulusActionPerformed

